package zakjo.studentsapp.model;public class Cities {    public int id;    public String name;    public String ref_gov;    public Cities() {    }    public Cities(int id, String name, String ref_gov) {        this.id = id;        this.name = name;        this.ref_gov = ref_gov;    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getRef_gov() {        return ref_gov;    }    public void setRef_gov(String ref_gov) {        this.ref_gov = ref_gov;    }}