package zakjo.studentsapp.model;public class Notifs {    public String adv_id ;    public String adv_text ;    public String adv_date ;    public String t_id ;    public Notifs() {    }    public Notifs(String adv_id, String adv_text, String adv_date, String t_id) {        this.adv_id = adv_id;        this.adv_text = adv_text;        this.adv_date = adv_date;        this.t_id = t_id;    }    public String getAdv_id() {        return adv_id;    }    public void setAdv_id(String adv_id) {        this.adv_id = adv_id;    }    public String getAdv_text() {        return adv_text;    }    public void setAdv_text(String adv_text) {        this.adv_text = adv_text;    }    public String getAdv_date() {        return adv_date;    }    public void setAdv_date(String adv_date) {        this.adv_date = adv_date;    }    public String getT_id() {        return t_id;    }    public void setT_id(String t_id) {        this.t_id = t_id;    }}